#Output recod.vcf files of different loci in different population.



for (( i=0; ${#vlist[@]} > $(($i*4)); ((i++)) ))
do
		for j in ${!glist[@]};
	do
		eval cg=$(echo \$\{${glist[$j]}\[\@\]\})
		if [[ (${vlist[$(($(($i*4))+2))]} == 0) || (${vlist[$(($(($i*4))+3))]} == 0) ]]
		then
			vcftools --gzvcf $PWD/ALL.chr${vlist[$(($(($i*4))+1))]}.phase3_shapeit2_mvncall_integrated_v5a.20130502.genotypes.vcf.gz --recode --out $PWD/tmp/${vlist[$(($i*4))]}_${glist[$j]} $cg --chr ${vlist[$(($(($i*4))+1))]} --snp ${vlist[$(($i*4))]}
		else
			vcftools --gzvcf $PWD/ALL.chr${vlist[$(($(($i*4))+1))]}.phase3_shapeit2_mvncall_integrated_v5a.20130502.genotypes.vcf.gz --recode --out $PWD/tmp/${vlist[$(($i*4))]}_${glist[$j]} $cg --chr ${vlist[$(($(($i*4))+1))]} --from-bp ${vlist[$(($(($i*4))+2))]} --to-bp ${vlist[$(($(($i*4))+3))]} --snp ${vlist[$(($i*4))]}
		fi
	done
	j=0
done

#Reform vcf files

for (( i=0; ${#vlist[@]} > $(($i*4)); ((i++)) ))
do
	eval title=$(echo 'GROUP\\tSAMPLE\\t%ID\\n')
	bcftools query -f $title $PWD/tmp/${vlist[$(($i*4))]}_${glist[$j]}.recode.vcf > $PWD/tmp/${vlist[$(($i*4))]}.tmp

	for j in ${!glist[@]};
	do
		eval entry=$(echo '['${glist[$j]}'\\t%SAMPLE\\t%TGT\\n]')
		bcftools query -f $entry $PWD/tmp/${vlist[$(($i*4))]}_${glist[$j]}.recode.vcf >> $PWD/tmp/${vlist[$(($i*4))]}.tmp
	done
done

vlist_aval=()

for (( i=0; ${#vlist[@]} > $(($i*4)); ((i++)) ))
do
	if [ -s $PWD/tmp/${vlist[$(($i*4))]}.tmp ]
		then
			vlist_aval=(${vlist_aval[@]} ${vlist[$(($i*4))]})
	fi
done

join -j 2 -t $'\t' $PWD/tmp/${vlist_aval[0]}.tmp $PWD/tmp/${vlist_aval[1]}.tmp | awk '{print $1 "\t" $2 "\t" $3 "\t" $5}' > $PWD/merge

for (( i=2; ${#vlist_aval[@]} > $i; ((i++)) ))
do
	awk '{print $2 "\t" $3}' $PWD/tmp/${vlist_aval[$i]}.tmp > $PWD/tmp/${vlist_aval[$i]}.del.tmp
	join -t $'\t' $PWD/merge $PWD/tmp/${vlist_aval[$i]}.del.tmp > $PWD/tmp/merge.tmp
	awk '{print $0}' $PWD/tmp/merge.tmp > $PWD/merge
	rm $PWD/tmp/${vlist_aval[$i]}.del.tmp
done

rm $PWD/tmp/merge.tmp

echo "Genotypes of loci ${vlist_aval[@]} in population ${glist[@]} are outputed."

exit 0




